package mxdeloitte.com.examples.clases;

public class Sumas {

	public Sumas() {
		super();
		// TODO Auto-generated constructor stub
	}

	public int suma(int a, int b) {
		return a + b;
	}
	public byte suma(byte a, byte b) {
		return (byte) (a + b);
	}
	public char suma(char a, char b) {
		return (char) (a + b);
	}
	public short suma(short a, short b) {
		return (short)(a + b);
	}
	public long suma(long a, long b) {
		return (a + b);
	}
	public float suma(float a, float b) {
		return (float)(a + b);
	}
	public double suma(double a, double b) {
		return (a + b);
	}
	public boolean suma(boolean a, boolean b) {
		return (a && b);
	}

}
